mapper.select:
SELECT a.*,b.name AS schoolName FROM departments a LEFT JOIN schools b ON a.school_id = b.id WHERE 1=1
AND a.school_id =#{schoolID}
AND a.name LIKE CONCAT('%',#{name},'%')
AND a.code LIKE CONCAT('%',#{code},'%')
AND a.status = #{status}
ORDER BY a.school_id,a.id

SELECT * FROM function_modules
WHERE 1=1

    AND name LIKE CONCAT('%',#{name},'%')


    AND status = #{status}


    AND path_type = #{pathType}


    AND is_need_login = #{isNeedLogin}

SELECT * FROM provinces
WHERE 1=1

    AND status = #{status}

SELECT a.*,b.name AS departmentName,b.school_id AS schoolID,c.name AS schoolName FROM specialities a LEFT JOIN departments b ON a.department_id = b.id LEFT JOIN schools c ON b.school_id = c.id WHERE 1=1
AND a.department_id =#{departmentID}
AND a.code LIKE CONCAT('%',#{code},'%')
AND a.name LIKE CONCAT('%',#{name},'%')
AND a.status = #{status}
ORDER BY b.school_id,a.department_id

SELECT a.*,b.`name` AS specialityName,c.`name` AS departmentName,d.`name` AS schoolName FROM `classes` a LEFT JOIN specialities b ON a.speciality_id = b.id LEFT JOIN departments c ON b.department_id = c.id LEFT JOIN schools d ON c.school_id = d.id WHERE 1=1
AND a.speciality_id =#{specialityID}
AND a.code LIKE CONCAT('%',#{code},'%')
AND a.name LIKE CONCAT('%',#{name},'%')
AND a.status = #{status}
AND a.begin_year = #{beginYear}
ORDER BY c.school_id,b.department_id,a.speciality_id
