mapper.select:
SELECT a.*,b.name AS departmentName FROM courses a
    LEFT JOIN departments b ON a.department_id = b.id
    WHERE 1=1

    AND a.name LIKE CONCAT('%',#{name},'%')


    AND a.department_id = #{departmentID}


    AND a.status = #{status}

mapper.update:
UPDATE courses SET name = #{name},department_id=#{departmentID},update_time = #{updateTime}

    ,introduction=#{introduction}


    ,cover=#{cover}

WHERE id=#{id}
